import react from "react";
import { useState } from "react";
import './Form.css'

export default function Form () {
    const [input, setInput] = useState({
        name:"",
        weight:"",
        height:"",
        lifeSpan:""
    })

    let handleInputChange = (e) => {
        setInput({
            ...input,
            [e.target.name]: e.target.value
        })
    }
    return (
        <form>

            <div className="field">
                <label>Name :</label>
                <input type="text" name="name" value={name} onChange={handleInputChange}/>
            </div>

            <div className="field">
                <label>Weight :</label>
                <input type="text" name="weight" value={weight} onChange={handleInputChange}/>
            </div>

            <div className="field">
                <label>Height :</label>
                <input type="text" name="height" value={height} onChange={handleInputChange}/>
            </div>

            <div className="field">
                <label>Life span :</label>
                <input type="text" name="lifeSpan" value={lifeSpan} onChange={handleInputChange}/>
            </div>

            <input type="submit" value="Submit"/>

        </form>
    )
}
