       //let  allDogsjson = allDogs.toJson()
        dispatch({
            type:"GET_DOGS",
            payload: allDogs//json
        })
        
    }
}

export function searchDog(name) {
    return async function(dispatch) {
        let breedName = (await axios.get(`http://localhost:3001/dogs?name=${name}`)).data;
        dispatch({
            type: "SEARCH_DOG",
            payload: breedName
        })
    }
}
export function getDogID(id) {
    return async (dispatch) => { 
        let dogID = (await axios.get(`http://localhost:3001/dogs/${id}`)).data
        dispatch({
            type: "GET_DOG_ID",
            payload: dogID
        })
    }
}

export function getTemperaments() {
    return async (dispatch) => {
        let dogTemperaments = (await axios.get('http://localhost:3001/temperament')).data
        dispatch({
            type: "GET_TEMPERAMENTS",
            payload: dogTemperaments
        })
    }
}

export  function reset() {
    return (dispatch) =>{
        dispatch({
            type: "RESET"
        }
        )
    }
    
}

export function createDog(dog, temperament) {
    return async (dispatch) => {
        try {
            /* let newDog = {
                name: dog.name,
                height: `${dog.minHeight} - ${dog.maxHeight}`,
                weight: `${dog.minWeight} - ${dog.maxWeight}`,
                temperament: temperament.temperament,
                lifeSpan: `${dog.minLifeSpan} - ${dog.maxLifeSpan}`
            } */
            let newDog = {
                name: dog.name,
                height: dog.height,
                weight: dog.weight,
                temperament: dog.temperament,
                lifeSpan: dog.lifeSpan
            }
            let resp = ''
            await axios.post('http://localhost:3001/dog', newDog)
            .then(response => resp = response.data)
            return dispatch({
                type: 'CREATE_DOG',
                payload: resp
            })

        } catch(error) {
            return console.log(error)
        }
    }
}

export function filterByTemps(temperament) {
    return {
        type: "FILTER_TEMPERAMENTS",
        payload: temperament
    }
}

export function filterTemps(selectedTemps) {
    return async (dispatch) => {
        const allDogs =  (await axios.get(`http://localhost:3001/dogs`)).data
        try {
            
            let splittedTemps = allDogs.map(dog =>  {
                let dogo = {
                    id: dog.id,
                    image: dog.image,
                    name: dog.name,
                    temperament: dog.temperament?.split(', '),
                    weight: dog.weight
                }
                return dogo
            })
            console.log('soy splitted', splittedTemps)
            console.log('soy selectedTemps', selectedTemps)
             
            
            let dogsWithTemp = []
            for( let i=0; i < selectedTemps.length; i++ ) {
                if (i < 1) {
                   let oneTemp = splittedTemps.filter(dog => dog.temperament?.includes(selectedTemps[i]))
                   console.log('soy oneTemp:', oneTemp)
                   dogsWithTemp = oneTemp
                }
                let othersTemps = dogsWithTemp.filter(dog => dog.temperament?.includes(selectedTemps[i]))
                dogsWithTemp = othersTemps
                
                    
               
                
            }
            let dogsFinal = dogsWithTemp.map( dog => {
                let dogconcat = {
                    name: dog.name,
                    id: dog.id,
                    weight: dog.weight,
                    temperaments: dog.temperaments.concat(', ')
                }
                return dogconcat
            })
            console.log('soy dogFinal:', dogsFinal)
            console.log('soy dogsWithTemp',dogsWithTemp)
            /* console.log('soy filtered', filtered) */
            return dispatch({
                type: 'FILTER_TEMPS',
                payload: dogsWithTemp
            })
        } catch(error) {
            console.log(error)
        }
    }
}

