import { useState } from "react"
import { useSelector } from "react-redux"


export function TempsForm () {
    const temps = useSelector(state => state.temperaments)
    const [selectedTemps, setSelectedTemps] = useState([])

    const handleTemps = (e) => {
        setSelectedTemps([...selectedTemps, e.target.value])
    }

   const onClose = (e) => {
    let newTemps = selectedTemps.filter(selected => selected !== e.target.value)
   }
    

    return (
        <div class='container'>
        <div class=" card card-body dark">
                    <div class="container text-center">
                        <div>
                            {
                                selectedTemps.map( temp => {
                                    return (
                                        <button type="button" key={temp} class="btn btn-dark">{temp}
                                        </button>
                                    )
                                })
                            }
                        </div>
                        <div class="row row-cols-auto">

                            {
                                temps.map((temp) =>
                                (
                                    <div class="col">
                                        <li class=" form-check" key={temp.id}>
                                            <input class="form-check-input" key={temp.id} type="checkbox" value={temp.name}
                                                onChange={handleTemps} id="flexCheckDefault" />
                                            <label class="form-check-label" for="flexCheckDefault">
                                                {temp.name}
                                            </label>
                                        </li>
                                    </div>
                                )
                                )
                            }
                        </div>
                    </div>
                </div>
        </div>
    )
}
