const initialState = {
    dogsLoaded: [],
    dogID: {},
    temperaments: [],
}

function order(array, prop) {
    let ordered = []
    let orderedZA = []
    if(prop === 'name') {
        ordered = array.sort(function (a, b) {
            if(a.name < b.name) {
                return -1
            }
            if(a.name > b.name) {
                return 1
            }
            return 0
        })
    }
    /* if(prop === 'weight') {
        ordered = array.sort(function (a, b) {
            
        })
    } */
}


export default  function rootReducer(state = initialState, action) {
    switch(action.type) {
        case 'GET_DOGS':
            return {
                ...state,
                dogsLoaded: action.payload
            }
        case 'SEARCH_DOG':
            return {
                ...state,
                dogsLoaded: action.payload
            }
        case 'GET_DOG_ID':
            return {
                ...state,
                dogID: action.payload
            }
        case 'RESET':
            return{
                ...state,
                dogID:{}

            } 
        case 'GET_TEMPERAMENTS':
            return {
                ...state,
                temperaments: action.payload
            }
        case 'FILTER_TEMPERAMENTS': // 
            if(action.payload === '') return {
                ...state
            }
            let dogbase = [...state.dogsLoaded]
            let filteredDogs = dogbase.filter( d => d.temperament?.includes(action.payload))
            return {
                ...state,
                dogsLoaded: filteredDogs
            }
        case 'ORDER_A_Z':
            let ordered = order([...state.dogsLoaded], 'name')
            return {
                ...state,
                dogsLoaded: ordered
            }
        case 'ORDER_Z_A':
            let orderedZA = order([...state.dogsLoaded], 'name').reverse()
            return {
                ...state,
                dogsLoaded: orderedZA
            }
                              
        default:
            return state    

    }

}
